import React from 'react';
import '../App.css';
import { Button } from './Button';
import './About.css';

function AboutSection() {
  return (
    <div className='about-container'>
    <div className='about-image-container'>
    </div>

    <h3>Founder: Adrian Hough</h3>
    <p>London-based Finance and Technology professional with a Bachelor of Commerce from the University of Melbourne (Australia) and a MBA from SDA Bocconi (Italy).</p>
    <br />
    <p>Finance, Risk and Change experience working in institutions such as Deutsche Bank, RBC Capital and Bank of America.</p>
    <br />
    <p>Delivered large-scale projects in asset-classes such as Commodities, Structured Finance, Derivatives, Fixed Income and Emerging Markets.</p>
    <br />
    <p>Winner of the 2019 CFTE | Onfido Entrepreneurship competition and member of CFTE alumni board.</p>
  </div>
  );
}

export default AboutSection;
