import React from 'react';
import '../App.css';
import { Button } from './Button';
import './SecModel.css';

function SecModelSection() {
  return (
    <div className='secmodel-container'>
      <div className='secmodel-project-container'>


<h3>Agile Project Tracker (First released March 2020)</h3>
<br />
<p><b>Summary</b> : This Securitisation model calculates cash flows and the annual Internal Rate of Returns (IRR) for an Asset Portfolio, Senior Bond (90% of Asset Portfolio) and Subordinated Loan (10% of Asset Portfolio). The Maturity is set at 30 years and Repayments are pro-rata and monthly.

This model includes an annual 1% fee calculated monthly on the Asset Portfolio balance and a Reserve Fund (1% of the original Asset Portfolio).

Recovery of Defaulted assets is set at 50% and is recognised in the same month as the default. The user sets the Amount of the Asset Portfolio, fixed coupons of the Assets, Senior Bond and Subordindated Loan and Prepayment and Default amounts.
Built using Python, Numpy, Pandas, Flask and deployed using Heroku.
</p>
<br />
<a href="https://secmodel.herokuapp.com/">https://secmodel.herokuapp.com/</a>
    </div>
      </div>
  );
}

export default SecModelSection;
