import React from 'react';
import '../App.css';
import { Button } from './Button';
import './About.css';

function AboutSection() {
  return (
    <div className='about-container'>

    <h1>About</h1>
    <br />
    <div className='about-hough-container'>
    <br />
    <p>London-based Finance and Technology startup which designs, scopes and delivers Finance and Risk solutions.  </p>
    <br />
    </div>
    <br />

    <div className='about-image-container'>
    </div>
    <br />
    <div className='about-adrian-container'>
    <h3>Founder:  Adrian Hough, BCom, MBA</h3>
    <br />
    <p>Finance, Risk and Change professional who has worked at Deutsche Bank, Centrica Energy, RBC Capital and Bank of America in London.</p>
    <br />
    <p>Successfully delivered Finance & Risk projects across asset classes such as Commodities, Structured Finance, Derivatives, Fixed Income and Emerging Markets.</p>
    <br />
    <p>Winner of the 2019 Centre for Finance, Technology and Entrepreneurship (CFTE) Onfido Extrapreneurship competition and member of the CFTE alumni committee.</p>
    </div>
    </div>


  );
}

export default AboutSection;
