import React from 'react';
import '../App.css';
import { Button } from './Button';
import './About.css';

function AboutSection() {
  return (
    <div className='about-container'>
    <h1>About</h1>
    <h3>Hough Fintech</h3>
    <p>London-based Finance and Technology startup which designs, scopes and delivers Finance and Risk change.  Please view 'Projects' for examples of MPV solutions for Projects and Financial Services.</p>
    <br />
    <h3>Values</h3>
    <p>Innovation - Passion for technology and how it can improve businesses and lives.  </p>
    <br />
    <p>Simplicity - Always search for the most simple solutions to improve understanding and ease-of-use.  </p>
    <br />
    <p>Ethical - Approach business with clear ethical principles and engage in disuscussions of Ethics.  </p>
    <div className='about-image-container'>
    </div>

    <h3>Founder: Adrian Hough</h3>

    <p>Finance, Risk and Change experience working in institutions such as Deutsche Bank, RBC Capital and Bank of America.</p>
    <br />
    <p>Delivered projects for Commodities, Structured Finance, Derivatives, Fixed Income and Emerging Markets businesses.</p>
    <br />
    <p>MBA and winner of the 2019 'CFTE Onfido Extrapreneurship' competition and member of CFTE alumni board.</p>
  </div>
  );
}

export default AboutSection;
